import './App.css';
import { useEffect } from 'react';
import { setItems } from './redux/store';
import { useDispatch } from 'react-redux';
import SearchBar from './components/SearchBar';
import ItemList from './components/ItemList';

function App() {
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchCountries = async () => {
      try {
        const response = await fetch('https://valid.layercode.workers.dev/list/countries?format=select&flags=true&value=code');
        const data = await response.json();
        dispatch(setItems(data.countries));
      } catch (error) {
        console.error("Error fetching countries:", error);
      }
    };

    fetchCountries();
  }, [dispatch]);

  return (
    <div className="App">
      <h1 style={{textAlign: 'center'}}>Country Code list</h1>
      div

      <SearchBar />
      <ItemList />
    </div>
  );
};

export default App;
