import React from "react";
import '../App.css';

const Item = ({ item }) => {
  const splittedItem = item.label.split(' ');

  return (
    <div className="item-container">
      <div style={{fontSize: '28px'}}>{splittedItem[0]}</div>
      <h4>{splittedItem[1]}</h4>
    </div>
  );
};

export default React.memo(Item);
