import logo from './logo.svg';
import './App.css';
import { useEffect } from 'react';
import { setItems } from './redux/store';

function App() {
  const dispatch = useDispatch();

  // Example: Mock data for the list of items.
  const items = [
    'React',
    'Redux',
    'JavaScript',
    'HTML',
    'CSS',
    'Node.js',
    'TypeScript',
    'GraphQL',
  ];

  // On initial load, set the items in the Redux store.
  useEffect(() => {
    dispatch(setItems(items));
  }, [dispatch, items]);

  return (
    <div className="App">
      <h1>Item Search</h1>
      <SearchBar />
      <ItemList />
    </div>
  );
};

export default App;
