import { createSlice, configureStore } from '@reduxjs/toolkit';

const itemsSlice = createSlice({
  name: 'items',
  initialState: {
    allItems: [], // Full fetched list of items from the API
    paginatedItems: [], // Items displayed in the UI (pagination chunks)
    page: 1, // Current page for pagination
    pageSize: 20, // Number of items per page
    searchTerm: '', // Search input
  },
  reducers: {
    setItems(state, action) {
      state.allItems = action.payload;
      state.paginatedItems = action.payload.slice(0, state.pageSize);
    },
    loadMoreItems(state) {
      const start = state.page * state.pageSize;
      const end = start + state.pageSize;
      state.paginatedItems = [...state.paginatedItems, ...state.allItems.slice(start, end)];
      state.page += 1;
    },
    setSearchTerm(state, action) {
      state.searchTerm = action.payload;
      state.page = 1;
      state.paginatedItems = state.allItems
        .filter(item => item.label.toLowerCase().includes(state.searchTerm.toLowerCase()))
        .slice(0, state.pageSize);
    },
  },
});

export const { setItems, loadMoreItems, setSearchTerm } = itemsSlice.actions;

const store = configureStore({
  reducer: {
    items: itemsSlice.reducer,
  },
});

export default store;
