import React, { useEffect, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Provider } from 'react-redux';
import store from './store';
import { setItems, loadMoreItems } from './store';
import SearchBar from './components/SearchBar';
import ItemList from './components/ItemList';

const App = () => {
  const dispatch = useDispatch();
  const { paginatedItems } = useSelector((state) => state.items);

  useEffect(() => {
    // Fetch the data from the API
    const fetchCountries = async () => {
      try {
        const response = await fetch('https://valid.layercode.workers.dev/list/countries?format=select&flags=true&value=code');
        const data = await response.json();
        dispatch(setItems(data.countries)); // Update Redux store with the fetched data
      } catch (error) {
        console.error("Error fetching countries:", error);
      }
    };

    fetchCountries();
  }, [dispatch]);

  // Infinite scroll logic
  const handleScroll = useCallback(() => {
    if (window.innerHeight + document.documentElement.scrollTop !== document.documentElement.offsetHeight) return;
    dispatch(loadMoreItems());
  }, [dispatch]);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [handleScroll]);

  return (
    <div className="App">
      <h1>Country List with Flags</h1>
      <SearchBar />
      <ItemList items={paginatedItems} />
    </div>
  );
};
