const Item = ({ item }) => {

  return (
    <div style={{width: '200px', height: '200px', border: '1px solid lightgray', cursor: 'pointer', borderRadius: '10px', textAlign: 'center', margin: '15px', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '20px', '&:hover' : {boxShadow: 'rgba(50, 50, 93, 0.25) 0px 50px 100px -20px, rgba(0, 0, 0, 0.3) 0px 30px 60px -30px, rgba(10, 37, 64, 0.35) 0px -2px 6px 0px inset'}}}>
      {item.label}
    </div>
  );
};

export default React.memo(Item);
